mixin section-shout(alignment, options)
  include ./section
  include ./intro
  include ../atoms/richtext
  include ../../utils/srcset
  include ../atoms/btn-primary
  include ../atoms/btn-secondary

  - var cls = [];
  - cls.push('section--shout')

  if alignment === ALIGNMENT_RIGHT
    - cls.push('section--shout-right');

  if alignment === ALIGNMENT_LEFT
    -cls.push('section--shout-left');

  .section(class=cls)
    .section-wrapper
      .section__content
        .shout
          .shout-wrapper
            .shout__picture
              +shout-for-action-image(options.image)
            .shout__text
              if options.title || options.intro
                  if options.title
                    h2.shout__text__title
                      if options.eyebrow
                        span.shout__text__title__eyebrow #{options.eyebrow}
                        span.accessibility :&nbsp;
                      span.shout__text__title__header #{options.title}

                  if options.intro
                    p.shout__text__intro !{options.intro}
                  if options.buttons
                    .shout__for__action__text__buttons
                      each button in options.buttons
                        .shout__for__action__buttons__button
                          if button.type === 'primary'
                            +btn-primary(button.label, button.href, "arrow")

                          else if button.type === 'secondary'
                            +btn-secondary(button.label, button.href, "")


