style.
  .display-icon {
    display: inline-block;
    border: 1px solid #ccc;
    background-color: whitesmoke;
    padding: 14px;
    text-align: center;
    margin: 3px;
    position: relative;
    min-width: 54px;
  }

  .display-icon:hover {
    z-index: 1;
  }

  [class^="icon-"] {
    transition: transform .15s ease-out;
  }

  .display-icon:hover [class^="icon-"] {
    transform: scale(1.1, 1.1);
  }

  .display-icon svg {
    fill: #111;
  }

